package com.javaex.practice;

public class Ex08 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		for(int dan=1; dan<=9; dan++) {
			System.out.println("");
			for(int j=2; j<=9; j++) {
				System.out.print(j + "*" + dan + "=" + dan*j + "  ");
			}
		}
	}

}
