package com.javaex.practice;

public class Ex09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0; i<10; i++) {
			System.out.println("");
			for(int j=1+i; j<=10+i; j++) {
				System.out.print(j);
				if (j>=10) {
					System.out.print("    ");
				} else { 
					System.out.print("     ");
				}
			}
		}
	}

}
